interface EnvironmentVariables {
  // Server listen port
  PORT: number;
  // Debug mode
  DEBUG_MODE: boolean;
  // Console log
  CONSOLE_LOG: boolean;

  // Cookie token
  COOKIE_TOKEN: string;

  // Swagger document title
  SWAGGER_TITLE: string;
  // Swagger document description
  SWAGGER_DESCRIPTION: string;
  // Swagger document version
  SWAGGER_VERSION: string;
}
